import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area } from "recharts";
import { Info, AlertCircle, CPU, Memory, HardDrive, Activity, Wifi } from "lucide-react";
import styles from "../styles/Dashboard.module.css";

const socket = io("http://localhost:5000");

const formatBytes = (bytes) => {
  if (!bytes && bytes !== 0) return 'N/A';
  if (bytes === 0) return '0 Bytes';
  const k = 1024;
  const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
};

const InfoTooltip = ({ title, content }) => (
  <div className="group relative inline-block ml-2">
    <Info size={16} className="text-gray-400 cursor-help" />
    <div className="hidden group-hover:block absolute z-10 w-64 p-2 bg-gray-800 text-white text-sm rounded-lg shadow-lg -left-1/2 transform -translate-x-1/2 mt-2">
      <div className="font-semibold mb-1">{title}</div>
      <div className="text-gray-300">{content}</div>
    </div>
  </div>
);

const MetricsCard = ({ icon: Icon, title, value, subtext, color = "white", tooltipContent }) => (
  <div className="bg-gray-800 rounded-lg p-4 shadow-lg transform hover:scale-105 transition-transform duration-200">
    <div className="flex items-center mb-2">
      <Icon size={24} className={`text-${color}-500 mr-2`} />
      <h3 className="text-lg font-semibold text-white">{title}</h3>
      {tooltipContent && <InfoTooltip title={title} content={tooltipContent} />}
    </div>
    <p className={`text-2xl font-bold text-${color}-500`}>{value}</p>
    {subtext && <p className="text-gray-400 text-sm mt-1">{subtext}</p>}
  </div>
);

const PredictionCard = ({ title, predictions }) => {
  if (!predictions) return null;
  
  return (
    <div className="bg-gray-800 rounded-lg p-4 shadow-lg">
      <h3 className="text-lg font-semibold text-white mb-3">{title} Predictions</h3>
      <div className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          {Object.entries(predictions.methods).map(([method, value]) => (
            <div key={method} className="bg-gray-700 p-2 rounded">
              <div className="text-gray-300 text-sm">{method.replace('_', ' ').toUpperCase()}</div>
              <div className="text-white font-semibold">{value.toFixed(1)}%</div>
              <div className="text-xs text-gray-400">
                Confidence: {predictions.confidence[method].toFixed(1)}%
              </div>
            </div>
          ))}
        </div>
        
        <div className="bg-gray-700 p-3 rounded">
          <div className="text-gray-300 mb-2">Ensemble Prediction</div>
          <div className="text-xl font-bold text-green-500">
            {predictions.ensemble.value.toFixed(1)}%
          </div>
          <div className="text-sm text-gray-400">
            Confidence: {predictions.ensemble.confidence.toFixed(1)}%
          </div>
        </div>
        
        <div className="grid grid-cols-2 gap-4 text-sm">
          <div className="bg-gray-700 p-2 rounded">
            <div className="text-gray-300">Trend</div>
            <div className="text-white">
              {predictions.metrics.trend > 0 ? '↗' : '↘'} {Math.abs(predictions.metrics.trend).toFixed(4)}
            </div>
          </div>
          <div className="bg-gray-700 p-2 rounded">
            <div className="text-gray-300">Volatility</div>
            <div className="text-white">{predictions.metrics.volatility.toFixed(4)}</div>
          </div>
          <div className="bg-gray-700 p-2 rounded">
            <div className="text-gray-300">R² Score</div>
                      <div className="text-white">{predictions.metrics.r2_score.toFixed(4)}</div>
                    </div>
                  </div>
                </div>
              </div>
            );}

            const Dashboard = () => {
                const [metrics, setMetrics] = useState({
                  cpu: { usage: 0, frequency: 0, cores: 0, stats: {} },
                  memory: { percent: 0, available: 0, total: 0, swap_percent: 0 },
                  gpu: { available: false, usage: 0, type: 'N/A', details: {} },
                  predictions: {
                    cpu: null,
                    memory: null,
                    gpu: null
                  },
                  system: {}
                });
                
                const [historicalData, setHistoricalData] = useState([]);
              
                useEffect(() => {
                  socket.on("update_metrics", (data) => {
                    try {
                      const parsedData = JSON.parse(data);
                      setMetrics(prevMetrics => ({
                        ...prevMetrics,
                        ...parsedData
                      }));
                      
                      setHistoricalData(prev => {
                        const newData = [...prev, {
                          time: new Date().toLocaleTimeString(),
                          cpu: parsedData.cpu?.usage || 0,
                          memory: parsedData.memory?.percent || 0,
                          gpu: parsedData.gpu?.usage || 0
                        }].slice(-30);
                        return newData;
                      });
                    } catch (error) {
                      console.error("Error processing metrics:", error);
                    }
                  });
              
                  return () => socket.off("update_metrics");
                }, []);
              
                return (
                  <div className="min-h-screen bg-gray-900 text-white p-6">
                    <div className="max-w-7xl mx-auto">
                      <header className="mb-8">
                        <h1 className="text-3xl font-bold text-gradient bg-clip-text text-transparent bg-gradient-to-r from-blue-500 to-purple-500">
                          System Performance Analytics
                        </h1>
                        <p className="text-gray-400 mt-2">
                          Real-time monitoring with ML-powered predictions
                        </p>
                      </header>
              
                      {/* System Information Panel */}
                      <div className="bg-gray-800 rounded-lg p-4 mb-8">
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                          <div>
                            <h3 className="text-lg font-semibold mb-2">System Info</h3>
                            <p className="text-gray-300">OS: {metrics.system?.os} {metrics.system?.os_version}</p>
                            <p className="text-gray-300">CPU: {metrics.system?.processor}</p>
                            <p className="text-gray-300">Cores: {metrics.cpu?.cores}</p>
                          </div>
                          <div>
                            <h3 className="text-lg font-semibold mb-2">GPU Details</h3>
                            <p className="text-gray-300">Type: {metrics.gpu?.type || 'Not detected'}</p>
                            <p className="text-gray-300">Model: {metrics.gpu?.details?.Name || 'N/A'}</p>
                            <p className="text-gray-300">Status: {metrics.gpu?.available ? 'Active' : 'Inactive'}</p>
                          </div>
                          <div>
                            <h3 className="text-lg font-semibold mb-2">Memory Overview</h3>
                            <p className="text-gray-300">Total RAM: {formatBytes(metrics.memory?.total)}</p>
                            <p className="text-gray-300">Available: {formatBytes(metrics.memory?.available)}</p>
                            <p className="text-gray-300">Swap Used: {formatBytes(metrics.memory?.swap_used)}</p>
                          </div>
                        </div>
                      </div>
              
                      {/* Main Metrics Grid */}
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                        <MetricsCard 
                          icon={CPU}
                          title="CPU Usage"
                          value={`${metrics.cpu?.usage?.toFixed(1) || '0'}%`}
                          subtext={`${metrics.cpu?.frequency?.toFixed(2) || '0'} MHz`}
                          color="blue"
                          tooltipContent="Real-time CPU utilization across all cores"
                        />
                        <MetricsCard 
                          icon={Memory}
                          title="Memory Usage"
                          value={`${metrics.memory?.percent?.toFixed(1) || '0'}%`}
                          subtext={`Available: ${formatBytes(metrics.memory?.available)}`}
                          color="purple"
                          tooltipContent="Current RAM usage including cached memory"
                        />
                        <MetricsCard 
                          icon={HardDrive}
                          title="GPU Usage"
                          value={metrics.gpu?.available ? `${metrics.gpu?.usage?.toFixed(1) || '0'}%` : 'N/A'}
                          subtext={`Type: ${metrics.gpu?.type || 'Not detected'}`}
                          color="green"
                          tooltipContent="Integrated GPU utilization"
                        />
                      </div>
              
                      {/* Predictions Section */}
                      <div className="mb-8">
                        <h2 className="text-2xl font-semibold mb-4">ML Predictions</h2>
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                          <PredictionCard title="CPU" predictions={metrics.predictions?.cpu} />
                          <PredictionCard title="Memory" predictions={metrics.predictions?.memory} />
                          <PredictionCard title="GPU" predictions={metrics.predictions?.gpu} />
                        </div>
                      </div>
              
                      {/* Usage Charts */}
                      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
                        <div className="bg-gray-800 rounded-lg p-4">
                          <h3 className="text-lg font-semibold mb-4">Resource Usage Trends</h3>
                          <ResponsiveContainer width="100%" height={300}>
                            <AreaChart data={historicalData}>
                              <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                              <XAxis dataKey="time" stroke="#9CA3AF" />
                              <YAxis stroke="#9CA3AF" />
                              <Tooltip 
                                contentStyle={{ backgroundColor: '#1F2937', border: 'none' }}
                                itemStyle={{ color: '#E5E7EB' }}
                              />
                              <Legend />
                              <Area type="monotone" dataKey="cpu" fill="#3B82F6" stroke="#60A5FA" fillOpacity={0.2} name="CPU" />
                              <Area type="monotone" dataKey="memory" fill="#8B5CF6" stroke="#A78BFA" fillOpacity={0.2} name="Memory" />
                              {metrics.gpu?.available && (
                                <Area type="monotone" dataKey="gpu" fill="#10B981" stroke="#34D399" fillOpacity={0.2} name="GPU" />
                              )}
                            </AreaChart>
                          </ResponsiveContainer>
                        </div>
              
                        <div className="bg-gray-800 rounded-lg p-4">
                          <h3 className="text-lg font-semibold mb-4">Prediction Accuracy</h3>
                          <ResponsiveContainer width="100%" height={300}>
                            <LineChart data={historicalData.slice(-10)}>
                              <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                              <XAxis dataKey="time" stroke="#9CA3AF" />
                              <YAxis stroke="#9CA3AF" />
                              <Tooltip 
                                contentStyle={{ backgroundColor: '#1F2937', border: 'none' }}
                                itemStyle={{ color: '#E5E7EB' }}
                              />
                              <Legend />
                              <Line type="monotone" dataKey="cpu" stroke="#3B82F6" name="Actual" />
                              <Line 
                                type="monotone" 
                                dataKey="predicted_cpu" 
                                stroke="#F59E0B" 
                                strokeDasharray="5 5" 
                                name="Predicted" 
                              />
                            </LineChart>
                          </ResponsiveContainer>
                        </div>
                      </div>
              
                      {/* CPU Stats Detail */}
                      <div className="bg-gray-800 rounded-lg p-4 mb-8">
                        <h3 className="text-lg font-semibold mb-4">CPU Performance Metrics</h3>
                        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                          <div className="bg-gray-700 p-3 rounded">
                            <p className="text-gray-400">Context Switches</p>
                            <p className="text-xl font-semibold">{metrics.cpu?.stats?.ctx_switches?.toLocaleString()}</p>
                          </div>
                          <div className="bg-gray-700 p-3 rounded">
                            <p className="text-gray-400">Interrupts</p>
                            <p className="text-xl font-semibold">{metrics.cpu?.stats?.interrupts?.toLocaleString()}</p>
                          </div>
                          <div className="bg-gray-700 p-3 rounded">
                            <p className="text-gray-400">Soft Interrupts</p>
                            <p className="text-xl font-semibold">{metrics.cpu?.stats?.soft_interrupts?.toLocaleString()}</p>
                          </div>
                          <div className="bg-gray-700 p-3 rounded">
                            <p className="text-gray-400">Syscalls</p>
                            <p className="text-xl font-semibold">{metrics.cpu?.stats?.syscalls?.toLocaleString()}</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              };
              
              export default Dashboard;
